import React, { useEffect, useContext } from 'react';
import { useParams } from 'react-router-dom';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';
import { bookContext } from './context/bookContext/bookContext';
import Navbar from './Navbar';
import Footer from './Footer';


// ... (import statements)

const BookDetails = () => {
  const { id } = useParams();
  const { book, getBookDetailsAction } = useContext(bookContext);

  useEffect(() => {
    getBookDetailsAction(id);
  }, [id, getBookDetailsAction]);

  return (
    <div>
      <Navbar />
      <div>
        {book ? (
          <>
            <div className="book-container mx-16 flex flex-row justify-start space-x-8">
              <Carousel showArrows={true} showThumbs={false} className="w-1/2">
                {book.images && book.images.map((image, index) => (
                  <div key={index} className="relative w-full h-96">
                    <img
                      src={image}
                      alt={`Book Image ${index + 1}`}
                      className="w-full h-full object-contain"
                    />
                  </div>
                ))}
              </Carousel>
              <div className="book-details-container mx-16 w-1/2">
                <h2 className="text-yellow-400 text-2xl tracking-widest font-semibold">{book.title}</h2>
                <h3 className="text-red-500 text-xl tracking-widest font-semibold">${book.price}</h3>
                <h3 className="text-lg">{book.author}</h3>
                <h3 className="text-sm">Condition: {book.condition}</h3>
                <hr />
                <p className="text-justify text-sm">{book.description}</p>
                <hr />
                <button className="bg-yellow-300 w-32 py-1.5 rounded-lg font-semibold">Buy Now</button>
              </div>
            </div>
          </>
        ) : (
          <p>Loading...</p>
        )}
      </div>
      <Footer />
    </div>
  );
};

export default BookDetails;
