import React from 'react'
import Navbar from './Navbar';
import Footer from './Footer';
import { Link } from 'react-router-dom';


const Profile = () => {
 
    const userAuthString = localStorage.getItem('userAuth');
    const userAuthD = userAuthString ? JSON.parse(userAuthString) : null;
    const createdAtAsDate = new Date(userAuthD.userFound.createdAt);
    

    const { fetchProfileAction, userAuth } = useContext(authContext);

  useEffect(() => {
    if (userAuthD.userFound._id) {
      fetchProfileAction(userAuthD.userFound._id);
    }
  }, [userAuthD.userFound._id, fetchProfileAction]);

// Extracting date and time from the createdAt property
    const createdAtDate = createdAtAsDate.toDateString();
  return (
    <div>
        <Navbar/>
        <div class="profile-container mx-16 flex flex-row justify-start space-x-10">

      <div class="image flex flex-col mt-4">
        <img src={userAuth.profilePicture} class="h-60 w-60 rounded-md mb-4"/>
        <h2 class="font-bold mb-4"> About me </h2>
        <p class="text-sm w-60 text-justify text-gray-500">
          {userAuth.bio}
        </p>

      </div>

      <div class="info mt-4">
        <div class="text-2xl tracking-widest bg-yellow-200 p-2 font-bold rounded-md px-4 w-32"> HELLO </div>
        <h2 class="p-2 text-2xl "> I'm <span class="text-2xl font-semibold"> {userAuth.name} </span> </h2>
        <div>
          <hr/>
        </div>
        <div class="p-2 mb-1">
           <span class="font-bold "> Location </span>:  Mumbai
        </div>
        <div class="p-2 mb-1">
           <span class="font-bold "> Email </span>  :   {userAuth.email}
        </div>
        <div class="p-2 mb-1">
           <span class="font-bold "> Registered On </span> :  {createdAtDate}
        </div>
        <div class="p-2 mb-1">
           <span class="font-bold "> Mobile no </span> :  9833924584
        </div>
        <div class="mb-5
        ">
          <hr/>
        </div> 
        <button class=" mx-2 p-1.5 px-4 bg-pink-300 rounded-md text-lg font-bold tracking-wide">
          Chat
        </button>
        <button class=" mx-2 p-1.5 px-4 bg-orange-300 rounded-md text-lg font-bold tracking-wide">
          Rate me
        </button>

      </div>

    </div>

    <h1 class="mx-16 text-2xl font my-4"> Latest Books </h1>
    <hr class="mx-16"/>
    
       
    {userAuth && userAuth.listings ? (
        <div className="flex flex-row justify-between mx-16 flex-wrap">
          {userAuth.listings.map((listing) => (
             <div class="relative flex flex-col mb-4">
             {listing.images && listing.images.length > 0 ? (
            <img
        class="m-2 w-60 h-44 rounded-2xl"
        src={listing.images[0]}
        alt={listing.title}
      />
    ) : (
      <Link to={`/upload-book-images/${listing._id}`}>
          <button className="m-2 w-60 h-44 rounded-2xl border-dashed border-2 flex items-center justify-center">
              Upload Image
          </button>
      </Link>
    )}
             <h3 class="text-sm font-bold mt-2 ml-2">{listing.title}</h3>
             <h3 class="text-sm font-bold ml-2 mt-1">${listing.price}</h3>
             <Link to={`/delete-book/${listing._id}`} >
             <button className="border-2 w-60 mt-2 rounded-lg p-2 bg-gray-200 ml-2 text-sm font-bold">
                  Delete Book
             </button>
             </Link>
             </div>
))}
        </div>
      ) : (
        <div>
          <h2  className='text-xl font-semibold mt-6 mb-6'> No Books Posted </h2>
        </div>
      )}

    <h1 class="mx-16 text-2xl font my-4"> Wishlists </h1>
    <hr class="mx-16"/>
    <div class="flex flex-row justify-between mx-16 flex-wrap"> 
    {userAuth  && userAuth.wishlists ? (
        <div className="flex flex-row justify-between mx-16 flex-wrap">
          {userAuth.wishlists.map((listing) => (
             <div class="relative flex flex-col mb-4">
             <img
                class="m-2 w-60 h-44 rounded-2xl"
                src={listing.image && listing.image.length > 0 ? listing.image[0] : "https://img.freepik.com/free-vector/opened-book-isolated-white-background_1308-78793.jpg?w=826&t=st=1704718771~exp=1704719371~hmac=0849e46e5b67647134aaad0a144391d424615d569cf3278bbc4ab6ab552a8f09"}
                alt={listing.title}
             />
             <h3 class="text-sm font-bold mt-2 ml-2">{listing.title}</h3>
             <h3 class="text-sm font-bold ml-2 mt-1">${listing.price}</h3>
             <button class="border-2 w-60 mt-2 rounded-lg p-2 bg-gray-200 ml-2 text-sm font-bold">
                Buy now
             </button>
             </div>
))}
        </div>
      ) : (
        <div>
          <h2  className='text-xl font-semibold mt-6 mb-6'> No Books Wishlisted </h2>
        </div>
      )}
    </div>


        <Footer/>
    </div>
  )
}

export default Profile ;
