import React, { useEffect, useContext } from 'react';
import { useParams } from 'react-router-dom';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';
import { bookContext } from './context/bookContext/bookContext';
import Navbar from './Navbar';
import Footer from './Footer';

const BookDetails = () => {
  const { id } = useParams();
  const { book, getBookDetailsAction } = useContext(bookContext);

  useEffect(() => {
    getBookDetailsAction(id);
  }, [id, getBookDetailsAction]);

  return (
    <div>
      <Navbar />
      <div>
        {book ? (
          <>
            <div className="book-container mx-16 flex flex-row justify-start space-x-8">
              <Carousel showArrows={true} showThumbs={false}>
                {book.images && book.images.map((image, index) => (
                  <div key={index}>
                    <img src={image} alt={`Book Image ${index + 1}`} />
                  </div>
                ))}
              </Carousel>
            </div>
            <div class="flex flex-col space-y-2 mx-16">
              <h2 class="text-yellow-400 text-2xl tracking-widest font-semibold">{book.title}</h2>
              <h3 class="text-red-500 text-xl tracking-widest font-semibold">${book.price}</h3>
              <h3 class="text-lg">{book.author}</h3>
              <h3 class="text-sm">Condition: {book.condition}</h3>
              <hr />
              <p class="text-justify text-sm">{book.description}</p>
              <hr />
              <button class="bg-yellow-300 w-32 py-1.5 rounded-lg font-semibold">Buy Now</button>
            </div>
          </>
        ) : (
          <p>Loading...</p>
        )}
      </div>
      <Footer />
    </div>
  );
};

export default BookDetails;
