import React, { useContext } from 'react'
import { useParams } from 'react-router-dom';
import { bookContext } from './context/bookContext/bookContext';
import Navbar from './Navbar';
import Footer from './Footer';


const UploadBookImages = () => {
  const {id} = useParams ; 
  const {uploadBookImageAction} = useContext(bookContext); 

  return (
    <div>
    <Navbar/>
    <hr className="mx-16" />
    <div className="m-16 flex flex-row justify-around">
      <div className="rounded-lg border-2 shadow-lg">
        <h1 className="m-3 text-center text-xl font-thin ">Upload Images</h1>
        <hr/>
        <form className="flex flex-col">
          <label for="images" className="mt-3 ml-3 font-mono text-gray-500 uppercase "> 
            Select Book Images
          </label>
          <input type="file"  multiple  name="images" className="m-3 rounded-md border-2 bg-gray-50 py-2 pl-2 pr-20 font-mono" placeholder=""/>
          <button className="m-3 rounded-md border-2 bg-black p-2 px-10 text-sm font-medium text-white hover:bg-gray-900">Upload </button>
        </form>
      </div>
    </div>
    <Footer/>
    </div>
    
  )
}

export default UploadBookImages ;
