import React, {  useContext } from 'react';
import { useParams } from 'react-router-dom';
import { bookContext } from './context/bookContext/bookContext';
import Navbar from './Navbar';
import Footer from './Footer';

const BookDetails = () => {
  const { id } = useParams();
  const { book, getBookDetailsAction } = useContext(bookContext);

  getBookDetailsAction(id); 

  return (
    <div>
      <Navbar />
      <div>
        {/* Render book details using the data from bookDetails */}
        <div class="flex overflow-scroll border-2 rounded-md">
         <img src={book.images[0]} class="h-96 w-96" />
         
      </div>
        <h2>{book.title}</h2> 
        {/* Include other details as needed */}
      </div>
      <Footer />
    </div>
  );
};

export default BookDetails;
