import React, { useEffect, useState } from 'react';

const ChatBox = ({ chat, currentUser }) => {
  const [userData, setUserData] = useState({});

  useEffect(() => {
    const user = chat?.members.find(member => member._id !== currentUser);
    setUserData(user || {});
  }, [chat, currentUser]);

  return (
    <div>
      <div className="w-3/4 bg-white rounded-lg shadow-lg p-4 flex-grow">
        <div className="flex items-center m-2 bg-blue-100 p-4 rounded-md">
          {/* Check if userData is available before accessing its properties */}
          {userData && (
            <>
              <img src={userData.profilePicture} alt="User" className="w-10 h-10 rounded-full mr-2" />
              <div className="flex flex-col">
                <span className="text-sm font-medium">{userData.name}</span>
              </div>
            </>
          )}
        </div>
        <div className="h-64 overflow-y-auto mb-4">
          {/* Messages */}
        </div>
        {/* Chat Input Area */}
        <form>
          <textarea className="w-full border border-gray-300 rounded-lg p-2" rows="3" placeholder="Type your message here..."></textarea>
          <button type="submit" className="mt-2 bg-blue-500 text-white py-2 px-4 rounded-lg">Send</button>
        </form>
      </div>
    </div>
  );
};

export default ChatBox;
