import React, { useEffect, useContext } from 'react';
import { useParams } from 'react-router-dom';
import { bookContext } from './context/bookContext/bookContext';
import { Carousel } from "@material-tailwind/react";
import 'react-responsive-carousel/lib/styles/carousel.min.css';
import Navbar from './Navbar';
import Footer from './Footer';

const BookDetails = () => {
  const { id } = useParams();
  const { book, getBookDetailsAction } = useContext(bookContext);

  useEffect(() => {
    getBookDetailsAction(id);
  }, [id, getBookDetailsAction]);

  return (
    <div>
      <Navbar/>

      <Carousel className="rounded-xl">
      <img
        src="https://images.unsplash.com/photo-1497436072909-60f360e1d4b1?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=2560&q=80"
        alt="image 1"
        className="h-full w-full object-cover"
      />
      <img
        src="https://images.unsplash.com/photo-1493246507139-91e8fad9978e?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=2940&q=80"
        alt="image 2"
        className="h-full w-full object-cover"
      />
      <img
        src="https://images.unsplash.com/photo-1518623489648-a173ef7824f3?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=2762&q=80"
        alt="image 3"
        className="h-full w-full object-cover"
      />
    </Carousel>

      <div>

        {book ? (
          <>
           
            

      <div class="book-container mx-16 flex flex-row justify-start space-x-8">

      <div className="flex overflow-scroll border-2 rounded-md">
              {book.images && book.images[0] && (
                <img src={book.images[0]} className="h-96 w-96" alt="Book Cover" />
              )}
      </div>

      
     
      <div class="flex flex-col space-y-2">
        
        <h2 class="text-yellow-400 text-2xl tracking-widest font-semibold"> {book.title}
        </h2>
        <h3 class="text-red-500 text-xl tracking-widest font-semibold">
          ${book.price}
        </h3>
        <h3 class=" text-lg"> {book.author}  </h3>
        <h3 class=" text-sm"> Condition: {book.condition}  </h3>
        <hr/>
        <p class="text-justify text-sm">
        {book.description}
        </p>
        <hr/>
        <button class="bg-yellow-300 w-32 py-1.5 rounded-lg font-semibold">
          Buy Now
        </button>
      </div>

      </div>
            
          </>
        ) : (
          <p>Loading...</p>
        )}

      </div>

      <Footer/>
    </div>
  );
};

export default BookDetails;
