import React, { useEffect, useContext, useState } from 'react';
import { useParams } from 'react-router-dom';
import { bookContext } from './context/bookContext/bookContext';
import Navbar from './Navbar';
import Footer from './Footer';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';

const BookDetails = () => {
  const { id } = useParams();
  const { book, getBookDetailsAction } = useContext(bookContext);

  const [selectedImage, setSelectedImage] = useState(0);

  useEffect(() => {
    getBookDetailsAction(id);
  }, [id, getBookDetailsAction]);

  const handleImageChange = (index) => {
    setSelectedImage(index);
  };

  return (
    <div>
      <Navbar/>

      <div>
        {book ? (
          <>
            <div className="book-container mx-16 flex flex-row justify-start space-x-8">
              <div className="flex overflow-scroll border-2 rounded-md">
                {book.images && book.images.length > 0 && (
                  <Carousel
                    selectedItem={selectedImage}
                    onChange={handleImageChange}
                  >
                    {book.images.map((image, index) => (
                      <div key={index}>
                        <img
                          src={image}
                          className="h-96 w-96 object-cover"
                          alt={`Book Image ${index + 1}`}
                        />
                      </div>
                    ))}
                  </Carousel>
                )}
              </div>

              <div className="flex flex-col space-y-2">
                <h2 className="text-yellow-400 text-2xl tracking-widest font-semibold">
                  {book.title}
                </h2>
                <h3 className="text-red-500 text-xl tracking-widest font-semibold">
                  ${book.price}
                </h3>
                <h3 className="text-lg">{book.author}</h3>
                <h3 className="text-sm">Condition: {book.condition}</h3>
                <hr />
                <p className="text-justify text-sm">{book.description}</p>
                <hr />
                <button className="bg-yellow-300 w-32 py-1.5 rounded-lg font-semibold">
                  Buy Now
                </button>
              </div>
            </div>
          </>
        ) : (
          <p>Loading...</p>
        )}
      </div>

      <Footer/>
    </div>
  );
};

export default BookDetails;
