import axios from "axios";
import { API_URL_CHAT } from "../../utils/apiURL";

const userChats = async(id) => {
    const config = {
        headers: {
          "Content-Type": "application/json",
        },
    };
    try {
        const chats = await axios.get(`${API_URL_CHAT}/${id}`,config); 
        return chats ; 
    } catch (error) {
        console.log(error); 
    }
}

export default userChats ;
