import React from 'react'
import Navbar from './Navbar'
import Footer from './Footer'
import { useContext , useState} from 'react'
import { authContext } from './context/AuthContext/AuthContext'

const Register = () => {
  const { registerUserAction } = useContext(authContext); 

  const [formData, setFormData] = useState({
    name:"",
    email: "",
    password: "",
    bio: "",
  });

  const { name , email, password , bio } = formData;


  const onChangeInput = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const onSubmitHandler = e => {
    e.preventDefault();
    //dispatch action
    registerUserAction(formData);
  };

  return (
    <div>
        <Navbar/>
        <div class="m-16 flex flex-row justify-around">
      <div class="rounded-lg border-2 shadow-lg">
        <h1 class="m-3 text-center text-xl font-thin ">Sign Up</h1>
        <form  onSubmit={onSubmitHandler}  class="flex flex-col">
          <input type="text" name="name" class="m-3 rounded-md border-2 bg-gray-50 py-2 pl-2 pr-20 font-mono" placeholder="Name" />
          <input type="text" name="name" class="m-3 rounded-md border-2 bg-gray-50 py-2 pl-2 pr-20 font-mono" placeholder="Email" />
          <input type="text" name="name" class="m-3 rounded-md border-2 bg-gray-50 py-2 pl-2 pr-20 font-mono" placeholder="Password" />
          <textarea rows="5" cols="10" name="bio" placeholder="Bio" class="m-3 rounded-md border-2 bg-gray-50 py-2 pl-2 pr-20 font-mono"> </textarea>
          <button class="m-3 rounded-md border-2 bg-black p-2 px-10 text-sm font-medium text-white hover:bg-gray-900" type=''>Sign Up</button>
        </form>
      </div>
    </div>
        <Footer/>
    </div>
  )
}

export default Register
