import React, { useEffect, useState } from 'react';
import { getMessages } from './chatHelper';
import { format } from "timeago.js";
import { useRef } from "react";
import {InputEmoji} from "react-input-emoji" ; 

const ChatBox = ({ chat, currentUser }) => {
  const otherUser = chat?.members.find(member => member._id !== currentUser);
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(true);


  useEffect(() => {
    const fetchMessages = async () => {
      try {
        const messagesData = await getMessages(chat._id);
        setMessages(messagesData.data);
        setLoading(false);
      } catch (error) {
        console.log(error);
        setLoading(false);
      }
    };

    if (chat !== null) {
      fetchMessages();
    }
  }, [chat]);

  useEffect(()=> {
    scroll.current?.scrollIntoView({ behavior: "smooth" });
  },[messages])
  
  const scroll = useRef();
  return (
    <div className="w-[75%] bg-white rounded-lg shadow-lg p-4 flex-grow">
      <div className="flex items-center m-2 bg-blue-100 p-4 rounded-md">
        <img src={otherUser?.profilePicture} alt="User" className="w-10 h-10 rounded-full mr-2" />
        <div className="flex flex-col">
          <span className="text-sm font-medium">{otherUser?.name}</span>
        </div>
      </div>
      <div className="chat-body  overflow-y-auto mb-4 flex flex-col gap-2 p-6" >
              { messages.map((message) => (
                <>
                  <div ref={scroll}
                  className={
                       message.senderId === currentUser
                            ? "message own bg-gradient-to-r from-cyan-400 to-light-blue-500 text-white"
                            : "message bg-gray-200 text-black"
                  }
                  >

                    <div>{message.text}</div>
                    <div>{format(message.createdAt)}</div>
                  </div>
                </>
              ))}
      </div>
    </div>
  );
};

export default ChatBox;
