import React, { useState , useContext } from 'react'
import Navbar from './Navbar'
import Footer from './Footer'
import { bookContext } from './context/bookContext/bookContext'
import bgVideo from "../assets/bgVideo.mp4" ; 
const Home = () => {

  const { searchBookAction } = useContext(bookContext); 
  const [formData , setBookName] = useState({
    bookName:""
  })
  
  const {bookName} = formData ; 

  const onChangeInput = (e) => {
    setBookName({ ...formData, [e.target.name]: e.target.value });
  };

  const onSubmitHandler = (e) => {
    e.preventDefault() ; 
    searchBookAction(formData); 
  }



  return (
    <div >
    <video autoPlay muted class="absolute top-0 left-0 z-0 h-100vh">
      <source src={bgVideo}/>
    </video>


    <div id="video-content" class="relative z-10 text-center p-6">
    <Navbar/>
    
    <div class="mx-16 my-16 flex flex-row justify-center items-center">
      <div class="text-center md:text-[65px] md:font-[400] font-varela  md:max-w-[650px] md:leading-[60px]  text-xl  text-white   hover:text-stroke-1 hover:text-transparent hover:text-stroke-white  transition duration-500 ">
        Discover the  perfect 
        book for You
      </div>
    </div>


    <div >
      <form onSubmit={onSubmitHandler} class="mx-16 my-8 flex flex-row items-center justify-center">
        <input onChange={onChangeInput} class="my-5 bg-transparent border md:p-2 md:px-28 p-1 text-center font-mono text-white rounded-full" type="text" name="bookName" placeholder="Search for Books" value={bookName} />
        <button class="hover:border-[1px] hover:border-white hover:px-4 py-2 hover:rounded-full border-transparent border-2 transition-all duration-500 text-white font-mono mx-2" type='submit'>Search</button>
      </form>
    </div>
    
    <Footer/>
    </div>
    
    </div>

  )
}

export default Home ;
