import React, { useEffect, useState, useRef } from 'react';
import { getMessages } from './chatHelper';
import {format} from "timeago.js" ; 

const ChatBox = ({ chat, currentUser }) => {
  const otherUser = chat?.members.find(member => member._id !== currentUser);
  const [messages, setMessages] = useState([]);
  const messageContainerRef = useRef(null);

  useEffect(() => {
    const fetchMessages = async () => {
      try {
        const mdata = await getMessages(chat._id); // Wait for the messages to be fetched
        setMessages(mdata);
      } catch (error) {
        console.log(error);
      }
    };

    if (chat !== null) {
      fetchMessages();
    }
  }, [chat]);

  useEffect(() => {
    // Scroll to the bottom of the message container whenever messages change
    if (messageContainerRef.current) {
      messageContainerRef.current.scrollTop = messageContainerRef.current.scrollHeight;
    }
  }, [messages]);

  return (
    <div className="w-[75%] bg-white rounded-lg shadow-lg p-4 flex-grow">
      <div className="flex items-center m-2 bg-blue-100 p-4 rounded-md">
        <img src={otherUser?.profilePicture} alt="User" className="w-10 h-10 rounded-full mr-2" />
        <div className="flex flex-col">
          <span className="text-sm font-medium">{otherUser?.name}</span>
        </div>
      </div>
      <div ref={messageContainerRef} className="h-64 overflow-y-auto mb-4">
  {/* Display messages */}
  {messages.map((message, index) => (
    <div
      key={index}
      className={`p-2 rounded-lg ${
        message.senderId === currentUser ? 'bg-blue-500 text-white self-end' : 'bg-gray-200 text-black self-start'
      }`}
    >
      <span>{message.text}</span>
      <span className="text-xs">{format(message.createdAt)}</span>
    </div>
  ))}
</div>

      <form>
        <textarea className="w-full border border-gray-300 rounded-lg p-2" rows="3" placeholder="Type your message here..."></textarea>
        <button type="submit" className="mt-2 bg-blue-500 text-white py-2 px-4 rounded-lg">Send</button>
      </form>
    </div>
  );
};

export default ChatBox;
