import { createContext , useReducer} from "react" ; 
import axios from "axios";
import { LOGIN_FAILED, LOGIN_SUCCESS , REGISTER_SUCCESS , REGISTER_FAIL } from "./AuthActionTypes";
import { API_URL_USER } from "../../../utils/apiURL";
export const authContext = createContext() ; 

const INITIAL_STATE = {
    userAuth: JSON.parse(localStorage.getItem('userAuth')) , 
    error: null , 
    loading: false , 
    profile: null,
}; 

const reducer = (state, action) => {
    const { type, payload } = action;
    switch (type) {
      //Register

      case REGISTER_SUCCESS:
      return {
        ...state,
        loading: false,
        error: null,
        userAuth: payload,
      };

    case REGISTER_FAIL:
      return {
        ...state,
        error: payload,
        loading: false,
        userAuth: null,
      };

      //Login
      case LOGIN_SUCCESS:
        //Add user to localstorage
        localStorage.setItem("userAuth", JSON.stringify(payload));
        return {
          ...state,
          loading: false,
          error: null,
          userAuth: payload,
        };

      case LOGIN_FAILED:
        return {
          ...state,
          error: payload,
          loading: false,
          userAuth: null,
        };

        case LOGOUT:
      //remove from storage
      localStorage.removeItem("userAuth");
      return {
        ...state,
        loading: false,
        error: null,
        userAuth: null,
      };

      // Profile
      default:
        return state;
    }
  };


const AuthContextProvider = ({children}) => {
    const [ state , dispatch] = useReducer(reducer,INITIAL_STATE); 

    // register action 
    const registerUserAction = async formData => {
        const config = {
          headers: {
            "Content-Type": "application/json",
          },
        };
        try {
          const res = await axios.post(
            `${API_URL_USER}/register`,
            formData,
            config
          );
          if (res?.data?.status === "success") {
            dispatch({
              type: REGISTER_SUCCESS,
              payload: res.data,
            });
          }
          //Redirect
          window.location.href = "/login";
        } catch (error) {
          dispatch({
            type: REGISTER_FAIL,
            payload: error?.response?.data?.message,
          });
        }
      };


    //login action 
    const loginUserAction =  async (formdata) => {
        const config = {
            headers : {
                "Content-Type":"application/json",
            }
        };
        try {
            const res = await axios.post(`${API_URL_USER}/login`,formdata,config); 
            if(res?.data?.status === "success")
            {
                dispatch({
                    type:LOGIN_SUCCESS,
                    payload: res.data,
                })
            }
            window.location.href = "/"
        } catch (error) {
            dispatch({
                type:LOGIN_FAILED,
                payload: error?.response?.data?.message,
            })
        }
    }

    return (
        
        <authContext.Provider value={{loginUserAction}}>
            {children}
        </authContext.Provider>
        
    ); 
}; 

export default AuthContextProvider ;
