import React from 'react'
import {Link} from "react-router-dom"
import { useContext , useEffect } from 'react'
import { authContext } from './context/AuthContext/AuthContext'
 

const Navbar = () => {
  
  const userAuthString = localStorage.getItem('userAuth');
  const userAuth = userAuthString ? JSON.parse(userAuthString) : null; // Parse the string to an object

  const { logoutUserAction } = useContext(authContext);

  const logoutFunc = () => {
    logoutUserAction(); 
  }

  if(userAuth)
  { 
    return (
      <div>
        <div class="navbar mx-16 my-2 flex md:flex-row md:justify-between md:items-end flex-col">
        <div class="md:text-2xl text-xl font-bold text-black">
          Online Book  <br className='hidden md:block'/>
          Thrift Store
        </div>
  
        <div class="my-3 flex flex-row space-x-5 font-thin">
          <Link to='/' class="hover:text-blue-500 hover:cursor-pointer"> Home </Link>
          <Link to='/buy'    class="hover:text-blue-500 hover:cursor-pointer"> Buy </Link>
          <Link to='/sell'   class="hover:text-blue-500 hover:cursor-pointer"> Sell</Link>
          <Link  onClick={logoutFunc}  class="hover:text-blue-500 hover:cursor-pointer"> Logout </Link>
          <Link to="/profile">
          <img class="w-10 h-10 p-1 rounded-full ring-2 ring-gray-300 dark:ring-gray-500 hidden md:block" src={userAuth.userFound.profilePicture} alt="Bordered avatar"></img>
          </Link>
          <Link to='/profile'   class="hover:text-blue-500 hover:cursor-pointer md:hidden"> Profile </Link>
        </div>
      </div>
      <hr class="mx-16" />
      </div>
    )
  }
  
  else
  {
    return (
      <div>
         <div class="navbar mx-16 my-2 flex flex-row justify-between items-end">
         <div class="md:text-2xl text-xl font-bold text-black">
          Online Book  <br className='hidden md:block'/>
          Thrift Store
        </div>
  
        <div class="my-3 flex flex-row space-x-5 font-thin">
          <Link to='/login'   class="hover:text-blue-500 hover:cursor-pointer"> Login </Link>
          <Link to='/sign-up'   class="hover:text-blue-500 hover:cursor-pointer"> Sign-Up </Link>
        </div>
      </div>
      <hr class="mx-16" />
      </div>
    )
  }
  
}

export default Navbar ;
