import React from 'react'
import {Link} from "react-router-dom"
import { useContext , useEffect } from 'react'
import { authContext } from './context/AuthContext/AuthContext'
 

const Navbar = () => {
  
  const userAuthString = localStorage.getItem('userAuth');
  const userAuth = userAuthString ? JSON.parse(userAuthString) : null; // Parse the string to an object

  const { logoutUserAction } = useContext(authContext);

  const logoutFunc = () => {
    logoutUserAction(); 
  }

  if(userAuth)
  { 
    return (
      <div>
  <div className="navbar mx-16 my-2 flex md:flex-row md:justify-between md:items-center flex-col ">
    <div className="md:text-3xl text-xl font-bold text-white hover:stroke-white hover:stroke-[2px] hover:text-transparent transition-all duration-500">
      Turning Leaves
    </div>

    <div className="my-3 flex flex-row space-x-5 font-thin items-center text-white md:text-xl md:space-x-10">
      <Link to='/' className="hover:border-[2px] hover:border-white hover:px-6 hover:py-2 hover:rounded-full  transition-all duration-500"> Home </Link>
      <Link to='/buy' className="hover:border-[2px] hover:border-white hover:px-6 hover:py-2 hover:rounded-full  transition-all duration-500"> Buy </Link>
      <Link to='/sell' className="hover:border-[2px] hover:border-white hover:px-6 hover:py-2 hover:rounded-full  transition-all duration-500"> Sell</Link>
      <Link onClick={logoutFunc} className="hover:border-[2px] hover:border-white hover:px-6 hover:py-2 hover:rounded-full  transition-all duration-500"> Logout </Link>
      <Link to="/profile" className="hidden md:block">
        <img className="w-10 h-10 p-1 rounded-full ring-2 ring-gray-300 dark:ring-gray-500" src={userAuth.userFound.profilePicture} alt="Bordered avatar" />
      </Link>
      <Link to='/profile' className="hover:text-blue-500 hover:cursor-pointer md:hidden"> Profile </Link>
    </div>
  </div>
</div>

    )
  }
  
  else
  {
    return (
      <div>
         <div class="mx-16 my-2 flex md:flex-row md:justify-between md:items-end flex-col">
         <div class="md:text-2xl text-xl font-bold text-black">
          Online Book  <br className='hidden md:block'/>
          Thrift Store
        </div>
  
        <div class="my-3 flex flex-row space-x-5 font-thin">
          <Link to='/login'   class="hover:text-blue-500 hover:cursor-pointer"> Login </Link>
          <Link to='/sign-up'   class="hover:text-blue-500 hover:cursor-pointer"> Sign-Up </Link>
        </div>
      </div>
      <hr class="mx-16" />
      </div>
    )
  }
  
}

export default Navbar ;
