// Frontend Component
import React, { useContext, useState } from 'react';
import Navbar from './Navbar';
import Footer from './Footer';
import { authContext } from './context/AuthContext/AuthContext';

const Register = () => {
  const { registerUserAction, generateOtpAction } = useContext(authContext);

  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    bio: '',
    otp: '', // New field for OTP
  });

  const { name, email, password, bio, otp } = formData;

  const onChangeInput = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const onGenerateOtp = async () => {
    // Call the generateOtpAction to generate and send OTP
    await generateOtpAction({ email });
    // Display a message or change UI to indicate OTP has been sent
  };

  const onSubmitHandler = (e) => {
    e.preventDefault();
    
    // Dispatch action with OTP included
    registerUserAction(formData);
  };

  return (
    <div>
      <Navbar />
      <div className="m-16 flex flex-row justify-around">
        <div className="rounded-lg border-2 shadow-lg">
          <h1 className="m-3 text-center text-xl font-thin">Sign Up</h1>
          <form onSubmit={onSubmitHandler} className="flex flex-col">
            <input
              onChange={onChangeInput}
              type="text"
              value={name}
              name="name"
              className="m-3 rounded-md border-2 bg-gray-50 py-2 pl-2 pr-20 font-mono"
              placeholder="Name"
            />
            <input
              onChange={onChangeInput}
              type="text"
              value={email}
              name="email"
              className="m-3 rounded-md border-2 bg-gray-50 py-2 pl-2 pr-20 font-mono"
              placeholder="Email"
            />
            <input
              onChange={onChangeInput}
              type="text"
              value={password}
              name="password"
              className="m-3 rounded-md border-2 bg-gray-50 py-2 pl-2 pr-20 font-mono"
              placeholder="Password"
            />
            <textarea
              onChange={onChangeInput}
              rows="5"
              value={bio}
              cols="10"
              name="bio"
              placeholder="Bio"
              className="m-3 rounded-md border-2 bg-gray-50 py-2 pl-2 pr-20 font-mono"
            ></textarea>
            {/* Display a button to generate OTP */}
            <button
              onClick={onGenerateOtp}
              className="m-3 rounded-md border-2 bg-black p-2 px-10 text-sm font-medium text-white hover:bg-gray-900"
              type="button"
            >
              Generate OTP
            </button>
            {/* Display the OTP field after generating OTP */}
            {otp && (
              <input
                onChange={onChangeInput}
                type="text"
                value={otp}
                name="otp"
                className="m-3 rounded-md border-2 bg-gray-50 py-2 pl-2 pr-20 font-mono"
                placeholder="Enter OTP"
              />
            )}
            <button
              className="m-3 rounded-md border-2 bg-black p-2 px-10 text-sm font-medium text-white hover:bg-gray-900"
              type="submit"
            >
              Sign Up
            </button>
          </form>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default Register;
