import React, { useEffect, useContext } from 'react';
import { useParams } from 'react-router-dom';
import { bookContext } from './context/bookContext/bookContext';
import Navbar from './Navbar';
import Footer from './Footer';

const BookDetails = () => {
  const { id } = useParams();
  const { book, getBookDetailsAction, error } = useContext(bookContext);

  useEffect(() => {
    getBookDetailsAction(id);
  }, [id, getBookDetailsAction]);

  if (!book) {
    // Loading state
    return (
      <div>
        <Navbar />
        <p>Loading...</p>
        <Footer />
      </div>
    );
  }

  if (error) {
    // Error state
    return (
      <div>
        <Navbar />
        <p>Error: {error}</p>
        <Footer />
      </div>
    );
  }

  // Rendering when book data is available
  return (
    <div>
      <Navbar />
      <hr className="mx-16 mb-8" />
      <div className="book-container mx-16 flex flex-row justify-start space-x-8">
      <div class="flex overflow-scroll border-2 rounded-md">
         <img src={book.images[0]} class="h-96 w-96"/>
      </div>
     
      <div class="flex flex-col space-y-2">
        <h2 class="text-yellow-400 text-2xl tracking-widest font-semibold"> {book.title}
        </h2>
        <h3 class="text-red-500 text-xl tracking-widest font-semibold">
          ${book.price}
        </h3>
        <h3 class=" text-lg"> {book.author} </h3>
        <h3 class=" text-sm"> Condition: {book.condition}  </h3>
        <hr/>
        <p class="text-justify text-sm">
          {book.description}
        </p>
        <hr/>
        <button class="bg-yellow-300 w-32 py-1.5 rounded-lg font-semibold">
          Buy Now
        </button>
      </div>
      </div>
      <Footer />
    </div>
  );
};

export default BookDetails;
