import React, { useContext, useState } from 'react';
import { Link } from 'react-router-dom';
import { authContext } from './context/AuthContext/AuthContext';

const Navbar = () => {
  const { logoutUserAction } = useContext(authContext);
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const userAuthString = localStorage.getItem('userAuth');
  const userAuth = userAuthString ? JSON.parse(userAuthString) : null;

  const logoutFunc = () => {
    logoutUserAction();
  };

  return (
    <div>
      <div className="navbar mx-4 md:mx-16 my-2 flex flex-row justify-between items-end">
        <div className="text-2xl font-bold text-black">
          Online Book <br />Thrift Store
        </div>

        {/* Conditional rendering based on user authentication */}
        {userAuth ? (
          <div className="my-3 flex flex-col md:flex-row space-y-3 md:space-y-0 md:space-x-5 font-thin">
            <Link to="/" className="hover:text-blue-500 hover:cursor-pointer">
              Home
            </Link>
            <Link to="/buy" className="hover:text-blue-500 hover:cursor-pointer">
              Buy
            </Link>
            <Link to="/sell" className="hover:text-blue-500 hover:cursor-pointer">
              Sell
            </Link>
            <button onClick={logoutFunc} className="hover:text-blue-500 hover:cursor-pointer">
              Logout
            </button>
            <Link to="/profile">
              <img
                className="w-10 h-10 p-1 rounded-full ring-2 ring-gray-300 dark:ring-gray-500"
                src={userAuth.userFound.profilePicture}
                alt="Bordered avatar"
              />
            </Link>
          </div>
        ) : (
          <div className="my-3 flex flex-row space-x-5 font-thin">
            <Link to="/login" className="hover:text-blue-500 hover:cursor-pointer">
              Login
            </Link>
            <Link to="/sign-up" className="hover:text-blue-500 hover:cursor-pointer">
              Sign-Up
            </Link>
          </div>
        )}

        {/* Hamburger menu icon for mobile */}
        <button
          onClick={() => setIsMenuOpen(!isMenuOpen)}
          className="md:hidden focus:outline-none"
        >
          <svg
            className="h-6 w-6 text-black"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 6h16M4 12h16m-7 6h7"></path>
          </svg>
        </button>
      </div>
      <hr className="mx-4 md:mx-16" />
    </div>
  );
};

export default Navbar;
