import React, { useEffect, useContext, useState } from 'react';
import { useParams } from 'react-router-dom';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';
import { bookContext } from './context/bookContext/bookContext';
import Navbar from './Navbar';
import Footer from './Footer';

const BookDetails = () => {
  const { id } = useParams();
  const { book, getBookDetailsAction, getBooksByUserAction } = useContext(bookContext);
  const [userBooks, setUserBooks] = useState([]);

  useEffect(() => {
    getBookDetailsAction(id);
    // Assuming you have access to the user ID, replace 'userId' with the actual user ID
    getBooksByUserAction(book.seller).then((books) => setUserBooks(books));
  }, [id, getBookDetailsAction, getBooksByUserAction]);


  return (
    <div>
      <Navbar />
      <div>
        {book ? (
          <>
            <div className="book-container mt-4 ml-16 mr-4 mb-16 flex flex-row justify-start space-x-8">
              <Carousel showArrows={true} showThumbs={false} className="w-1/2">
                {book.images && book.images.map((image, index) => (
                  <div key={index} className="relative w-full h-96">
                    <img
                      src={image}
                      alt={`Book Image ${index + 1}`}
                      className="w-full h-full object-contain"
                    />
                  </div>
                ))}
              </Carousel>
              <div class="flex flex-col space-y-2">
        
        <h2 class="text-yellow-400 text-2xl tracking-widest font-semibold"> {book.title}
        </h2>
        <h3 class="text-red-500 text-xl tracking-widest font-semibold">
          ${book.price}
        </h3>
        <h3 class=" text-lg"> {book.author}  </h3>
        <h3 class=" text-sm"> Condition: {book.condition}  </h3>
        <hr/>
        <p class="text-justify text-sm">
        {book.description}
        </p>
        <hr/>
        <button class="bg-yellow-300 w-32 py-1.5 rounded-lg font-semibold">
          Buy Now
        </button>
      </div>
            </div>
          </>
        ) : (
          <p>Loading...</p>
        )}
      </div>
      <h1 className="mx-16 text-2xl font my-4"> More Books by the User </h1>
      <hr className="mx-16" />
      <div className="flex flex-row justify-between mx-16 flex-wrap">
        {userBooks.map((userBook) => (
          <div key={userBook.id} className="flex flex-col mb-4">
            <img className="m-2 w-60 h-44 rounded-2xl" src={userBook.image} alt={userBook.title} />
            <h3 className="text-sm font-bold mt-2 ml-2">{userBook.title}</h3>
            <h3 className="text-sm font-bold ml-2 mt-1">${userBook.price}</h3>
            <button className="border-2 w-60 mt-2 rounded-lg p-2 bg-gray-200 ml-2 text-sm font-bold">
              Buy now
            </button>
          </div>
        ))}
      </div>
      <Footer />
    </div>
  );
};

export default BookDetails;
