import React, { useContext, useEffect, useState } from 'react';
import Navbar from './Navbar';
import Footer from './Footer';
import { Link } from 'react-router-dom';
import { authContext } from './context/AuthContext/AuthContext';
import axios from 'axios';
import { API_URL_USER } from '../utils/apiURL';

const Profile = () => {
  const userAuthString = localStorage.getItem('userAuth');
  const userAuth = userAuthString ? JSON.parse(userAuthString) : null;
  const createdAtAsDate = new Date(userAuth.userFound.createdAt);
  const createdAtDate = createdAtAsDate.toDateString();

  const { userProfile, getUserProfile } = useContext(authContext);
  const userId = userAuth.userFound._id;

  useEffect(() => {
    getUserProfile(userId);
  }, [userId, getUserProfile]);

  const returnReviewerData = async (id) => {
    const config = {
      headers: {
        'Content-Type': 'application/json',
      },
    };
    try {
      const ReviewerData = await axios.get(`${API_URL_USER}/${id}`, config);
      return ReviewerData.data.data.name;
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div>
      <Navbar />
      <div className="max-w-4xl mx-auto bg-white shadow-md rounded-lg p-6">
        <div className="relative">
          <div
            className="h-64 bg-cover bg-center rounded-t-lg"
            style={{ backgroundImage: 'url(header-bg.jpg)' }}
          ></div>
          <img
            src={userProfile.profilePicture || 'profile-picture.jpg'}
            alt="Profile Picture"
            className="w-32 h-32 rounded-full absolute bottom-0 left-1/2 transform -translate-x-1/2 translate-y-1/2 border-4 border-white"
          />
        </div>
        <div className="mt-16 text-center">
          <h2 className="text-2xl font-bold">{userProfile.name}</h2>
          <p className="text-gray-600">{userProfile.bio}</p>
        </div>
        <div className="mt-8">
          <p className="text-gray-600">Email: {userProfile.email}</p>
          <p className="text-gray-600">Phone: (123) 456-7890</p>
          <p className="text-gray-600">
            Address: 123 Main St, Anytown, USA 12345
          </p>
          <p className="text-gray-600">Joined: {createdAtDate}</p>
        </div>
        <Link to="/update-user-profile">
          <button className="ease-in-out transform hover:scale-105 duration-300 mx-3 my-3 p-1.5 px-8 bg-pink-300 rounded-md text-lg font-bold tracking-wide hover:bg-pink-400">
            Edit
          </button>
        </Link>
      </div>

      <h1 className="mx-16 text-2xl font my-4">Latest Books</h1>
      <hr className="mx-16" />

      {userProfile && userProfile.listings ? (
        <div className="flex flex-row mx-16 flex-wrap">
          {userProfile.listings.map((listing) => (
            <div
              key={listing.id}
              className="relative mr-5 mb-5 bg-white border border-gray-200 rounded-lg shadow ease-in-out transform hover:scale-105 duration-300"
            >
              {listing.images && listing.images.length > 0 ? (
                <img
                  className="w-72 h-72"
                  src={listing.images[0]}
                  alt="product image"
                />
              ) : (
                <div>
                  <Link to={`/upload-book-images/${listing._id}`}>
                    <button className="w-72 h-72">Upload Image</button>
                  </Link>
                </div>
              )}

              <div className="px-5 pb-5">
                <h5 className="text-xl font-semibold tracking-tight text-gray-900">
                  {listing.title}
                </h5>

                <div className="flex items-center justify-between my-2">
                  <span className="text-3xl font-bold text-gray-900">
                    {listing.price}
                  </span>
                  <Link
                    to={`/book-details/${listing._id}`}
                    className="text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 font-medium rounded-lg text-sm px-5 py-2.5 text-center"
                  >
                    Buy Now
                  </Link>
                </div>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <div>
          <h2 className="text-xl font-semibold mt-6 mb-6">No Books Posted</h2>
        </div>
      )}

      <h1 className="mx-16 text-2xl font my-4">Wishlists</h1>
      <hr className="mx-16" />
      <div className="">
        {userProfile && userProfile.wishlist ? (
          <div className="flex flex-row mx-16 flex-wrap">
            {userProfile.wishlist.map((listing) => (
              <div
                key={listing.id}
                className="relative mr-5 mb-5 bg-white border border-gray-200 rounded-lg shadow ease-in-out transform hover:scale-105 duration-300"
              >
                {listing.images && listing.images.length > 0 ? (
                  <img
                    className="w-72 h-72"
                    src={listing.images[0]}
                    alt="product image"
                  />
                ) : (
                  <div>
                    <h2>No Image</h2>
                  </div>
                )}

                <div className="px-5 pb-5">
                  <h5 className="text-xl font-semibold tracking-tight text-gray-900">
                    {listing.title}
                  </h5>

                  <div className="flex items-center justify-between my-2">
                    <span className="text-3xl font-bold text-gray-900">
                      {listing.price}
                    </span>
                    <Link
                      to={`/book-details/${listing._id}`}
                      className="text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 font-medium rounded-lg text-sm px-5 py-2.5 text-center"
                    >
                      Buy Now
                    </Link>
                  </div>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div>
            <h2 className="text-xl font-semibold mt-6 mb-6">
              No Books Wishlisted
            </h2>
          </div>
        )}
      </div>
      <Footer />
    </div>
  );
};

export default Profile;
