import React from 'react'
import Footer from '../Footer'
import {useContext ,  useState} from 'react'
import { authContext } from './AuthContext/AuthContext'
import Navbar from '../Navbar'
import { useLocation } from 'react-router-dom';


const OTPverify = () => {
   
  const { registerUserAction  } = useContext(authContext); 
  const [otpformData, setotpFormData] = useState({
    userOtp: 0,
  });

  /*const queryParams = new URLSearchParams(window.location.search);
  const otp = queryParams.get('otp');
  const formDataString = queryParams.get('formData');
  const formData = JSON.parse(formDataString);*/

  const location = useLocation();
  const { otp, formData } = location.state;

  const onChangeInput = e => {
    setotpFormData({...otpformData , [e.target.name]: e.target.value });
  };

  const onSubmitHandler = e => {

    e.preventDefault();
    console.log(otp); 
    console.log(otpformData.userOtp); 

    if(otp === otpformData.userOtp)
    {
        registerUserAction(formData);
    }
    else
    {
        window.alert("Oops, Wrong Otp Entered !!! "); 
        window.location.href = "/sign-up" ; 
    }

  };


  return (
    <div>
        <Navbar/>
    
    <div class="relative flex min-h-screen flex-col justify-center overflow-hidden bg-gray-50 py-12">
  <div class="relative bg-white px-6 pt-10 pb-9 shadow-xl mx-auto w-full max-w-lg rounded-2xl">
    <div class="mx-auto flex w-full max-w-md flex-col space-y-16">
      <div class="flex flex-col items-center justify-center text-center space-y-2">
        <div class="font-semibold text-3xl">
          <p>Email Verification</p>
        </div>
        <div class="flex flex-row text-sm font-medium text-gray-400">
          <p>We have sent a code to your email {formData.email}</p>
        </div>
      </div>

      <div>
        <form onSubmit={onSubmitHandler}>
          <div class="flex flex-col space-y-16">
            <div class="flex flex-row items-center justify-between mx-auto w-full max-w-xs">
                <input onChange={onChangeInput} name="userOtp" value={otpformData.userOtp} class="w-full h-full flex flex-col items-center justify-center text-center px-5 py-2 outline-none rounded-xl border border-gray-200 text-lg bg-white focus:bg-gray-50 focus:ring-1 ring-blue-700" type="number" />
           </div>

            <div class="flex flex-col space-y-5">
              <div>
                <button type="submit" class="flex flex-row items-center justify-center text-center w-full border rounded-xl outline-none py-5 bg-blue-700 border-none text-white text-sm shadow-sm">
                  Verify Account
                </button>
              </div>

              <div class="flex flex-row items-center justify-center text-center text-sm font-medium space-x-1 text-gray-500">
                <p>Didn't recieve code?</p> <a class="flex flex-row items-center text-blue-600" href="http://" target="_blank" rel="noopener noreferrer">Resend</a>
              </div>
            </div>
          </div>
        </form>
      </div>
    </div>
  </div>
</div>

        <Footer/>
    </div>
  )
}

export default OTPverify ;
