import React, { useState, useEffect } from 'react';
import Navbar from '../Navbar';
import Footer from '../Footer';
import userChats from './chatHelper';

const Chat = () => {
  const userAuthString = localStorage.getItem('userAuth');
  const userAuth = userAuthString ? JSON.parse(userAuthString) : null;
  const user = userAuth?.userFound;

  const [chats, setChats] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchChats = async () => {
      try {
        if (user) {
          const chatsData = await userChats(user._id);
          setChats(chatsData.data);
          setLoading(false); // Data fetched, loading complete
        }
      } catch (error) {
        console.log('Error fetching chats:', error);
        setLoading(false); // Error occurred, loading complete
      }
    };

    fetchChats();
  }, [user]);

  return (
    <div>
      <Navbar />
      
      <div className="container mx-16 flex justify-center items-stretch mt-4 space-x-2">
        {/* Left Section */}
        <div className="w-[15%] bg-white rounded-lg shadow-lg p-4 flex-grow">
          {/* Title */}
          <h1 className="text-2xl font-semibold mb-4">Chats</h1>
          {/* Check if loading is true */}
          {loading ? (
            // Show loading spinner while data is being fetched
            <div className="flex justify-center items-center">
              <div className="loader ease-linear rounded-full border-8 border-t-8 border-gray-200 h-16 w-16"></div>
            </div>
          ) : (
            // Data fetched, render user list
            <>
              <hr />
              <div className="mb-4">
                {chats.map(chat => (
                  <div key={chat._id} className="flex items-center m-2 bg-yellow-100 p-4 rounded-md">
                    {/* Render the other user's profile picture */}
                    <img src={chat.members.filter(member => member._id !== user._id)[0].profilePicture} alt="User" className="w-10 h-10 rounded-full mr-2" />
                    <div className="flex flex-col">
                      {/* Render the other user's name */}
                      <span className="text-sm font-medium">{chat.members.filter(member => member._id !== user._id)[0].name}</span>
                      {/* You can render other details here like online status */}
                      <span className="text-sm font-light">{/* Add online status here */}</span>
                    </div>
                  </div>
                ))}
              </div>
            </>
          )}
        </div>
        <div class=" bg-white rounded-lg shadow-lg p-4 flex-grow">
            <div class="flex items-center m-2 bg-blue-100 p-4 rounded-md">
                    <img src="https://img.freepik.com/free-vector/coding-concept-illustration_114360-1155.jpg?w=740&t=st=1707747166~exp=1707747766~hmac=72b73ff746dbb98af2d6019de275f4367e6c2dcf6e1a968b092e7b750c137609" alt="User 2" class="w-10 h-10 rounded-full mr-2"/>
                    <div class="flex flex-col">
                      <span class="text-sm font-medium">Krishna Mistry</span>
                     
                    </div>
            </div>
            <div class="h-64 overflow-y-auto mb-4">
            </div>
            <form>
                <textarea class="w-full border border-gray-300 rounded-lg p-2" rows="3" placeholder="Type your message here..."></textarea>
                <button type="submit" class="mt-2 bg-blue-500 text-white py-2 px-4 rounded-lg">Send</button>
            </form>
        </div>
      </div>

      <Footer />
    </div>
  );
};

export default Chat;
