import React, { useEffect, useContext } from 'react';
import { useParams } from 'react-router-dom';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';
import { bookContext } from './context/bookContext/bookContext';
import Navbar from './Navbar';
import Footer from './Footer';


// ... (import statements)

const BookDetails = () => {
  const { id } = useParams();
  const { book, getBookDetailsAction } = useContext(bookContext);

  useEffect(() => {
    getBookDetailsAction(id);
  }, [id, getBookDetailsAction]);

  return (
    <div>
      <Navbar />
      <div>
        {book ? (
          <>
            <div className="book-container mt-4 ml-16 mr-4 mb-16 flex flex-row justify-start space-x-8">
              <Carousel showArrows={true} showThumbs={false} className="w-1/2">
                {book.images && book.images.map((image, index) => (
                  <div key={index} className="relative w-full h-96">
                    <img
                      src={image}
                      alt={`Book Image ${index + 1}`}
                      className="w-full h-full object-contain"
                    />
                  </div>
                ))}
              </Carousel>
              <div class="flex flex-col space-y-2">
        
        <h2 class="text-yellow-400 text-2xl tracking-widest font-semibold"> {book.title}
        </h2>
        <h3 class="text-red-500 text-xl tracking-widest font-semibold">
          ${book.price}
        </h3>
        <h3 class=" text-lg"> {book.author}  </h3>
        <h3 class=" text-sm"> Condition: {book.condition}  </h3>
        <hr/>
        <p class="text-justify text-sm">
        {book.description}
        </p>
        <hr/>
        <button class="bg-yellow-300 w-32 py-1.5 rounded-lg font-semibold">
          Buy Now
        </button>
      </div>
            </div>
          </>
        ) : (
          <p>Loading...</p>
        )}
      </div>
      <h1 class="mx-16 text-2xl font my-4"> More Books by the User </h1>
    <hr class="mx-16"/>
    <div class="flex flex-row justify-between mx-16 flex-wrap"> 
      <div class=" flex flex-col mb-4">
         <img class="m-2 w-60 h-44 rounded-2xl" src="https://img.freepik.com/free-vector/quantum-computing-abstract-concept-illustration_335657-3847.jpg?w=740&t=st=1702013756~exp=1702014356~hmac=05be7c5b4fd10c97379ac39b3586f6959452c598eb2882d7fcc899cc233e6fb3"/>
         <h3 class="text-sm font-bold mt-2 ml-2"> Atomic Habits </h3>
         <h3 class="text-sm font-bold ml-2 mt-1"> $9 </h3>
         <button class="border-2 w-60 mt-2 rounded-lg p-2 bg-gray-200 ml-2 text-sm font-bold"> Buy now </button>
      </div>

      <div class="relative flex flex-col mb-4">
         <img class="m-2 w-60 h-44 rounded-2xl" src="https://img.freepik.com/free-vector/background-about-physics_1284-698.jpg?w=740&t=st=1702013821~exp=1702014421~hmac=3ab653857a67745d756d5be2d119071dbe4a49d052abd3db6a722f56de595668"/>
         <h3 class="text-sm font-bold mt-2 ml-2"> Ncert Physics Class 11 </h3>
         <h3 class="text-sm font-bold ml-2 mt-1"> $2 </h3>
         <button class="border-2 w-60 mt-2 rounded-lg p-2 bg-gray-200 ml-2 text-sm font-bold"> Buy now </button>
      </div>

      <div class="relative flex flex-col mb-4">
         <img class="m-2 w-60 h-44 rounded-2xl" src="https://img.freepik.com/free-vector/vintage-books-with-paper-scroll-feather-ink-pot-colored-sketch-decorative-concept-vector-illustration_1284-2997.jpg?w=740&t=st=1702014374~exp=1702014974~hmac=d1cc55ed07db5cac889289595ab529e738bdc4ab0087c4f1fe351b69cced5e19"/>
         <h3 class="text-sm font-bold mt-2 ml-2"> History </h3>
         <h3 class="text-sm font-bold ml-2 mt-1"> $1 </h3>
         <button class="border-2 w-60 mt-2 rounded-lg p-2 bg-gray-200 ml-2 text-sm font-bold"> Buy now </button>
      </div>

      <div class="relative flex flex-col mb-4">
         <img class="m-2 w-60 h-44 rounded-2xl" src="https://img.freepik.com/free-photo/magic-fairytale-book-concept_23-2150171876.jpg?w=996&t=st=1702014480~exp=1702015080~hmac=602dd36ce0454642c28cd31533a4855435a8194b4fa8a26272c7201dbe45f364"/>
         <h3 class="text-sm font-bold mt-2 ml-2"> Fiction </h3>
         <h3 class="text-sm font-bold ml-2 mt-1"> $3 </h3>
         <button class="border-2 w-60 mt-2 rounded-lg p-2 bg-gray-200 ml-2 text-sm font-bold"> Buy now </button>
      </div>
      </div>
      <Footer />
    </div>
  );
};

export default BookDetails;
