import React, { useState, useEffect, useRef } from 'react';
import Navbar from '../Navbar';
import Footer from '../Footer';
import ChatBox from './ChatBox';
import { userChats } from './chatHelper';
import { io } from "socket.io-client";

const Chat = () => {

  const socket = useRef(); 
  const userAuthString = localStorage.getItem('userAuth');
  const userAuth = userAuthString ? JSON.parse(userAuthString) : null;
  const user = userAuth?.userFound;

  const [chats, setChats] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentChat, setCurrentChat] = useState(null);
  const [onlineUsers, setOnlineUsers] = useState([]);
  const [sendMessage, setSendMessage] = useState(null);
  const [receivedMessage, setReceivedMessage] = useState(null);

  useEffect(() => {
    const fetchChats = async () => {
      try {
        if (user) {
          const chatsData = await userChats(user._id);
          setChats(chatsData.data);
          setLoading(false); // Data fetched, loading complete
        }
      } catch (error) {
        console.log('Error fetching chats:', error);
        setLoading(false); // Error occurred, loading complete
      }
    };

    fetchChats();
  }, [user]);

  // Connect to Socket.io
  useEffect(() => {
    socket.current = io("ws://localhost:8800");
    socket.current.emit("new-user-add", user._id);
    socket.current.on("get-users", (users) => {
      setOnlineUsers(users);
    });
  }, [user]);

  // Send Message to socket server
  useEffect(() => {
    if (sendMessage!==null) {
      socket.current.emit("send-message", sendMessage);}
  }, [sendMessage]);

  // Get the message from socket server
  useEffect(() => {
    socket.current.on("recieve-message", (data) => {
      console.log(data)
      setReceivedMessage(data);
    }
    );
  }, []);

  const checkOnlineStatus = (chat) => {
    const chatMember = chat.members.find((member) => member !== user._id);
    const online = onlineUsers.find((user) => user.userId === chatMember);
    return online ? true : false;
  };

  return (
    <div>
      <Navbar />
      <div className="container mx-auto flex justify-center items-stretch mt-4 mb-4">
        {/* Left Section */}
        <div className="mx-16 w-[25%] bg-white rounded-lg shadow-lg p-4 mr-4 flex-grow">
          <h1 className="text-2xl font-semibold mb-4">Chats</h1>
          {/* Check if loading is true */}
          {loading ? (
            // Show loading spinner while data is being fetched
            <div className="flex justify-center items-center h-screen">
              <div className="animate-spin rounded-full h-16 w-16 border-t-2 border-b-2 border-gray-900"></div>
            </div>
          ) : (
            // Data fetched, render user list
            <>
              <hr />
              <div className="mb-4">
                {chats.map(chat => (
                  <div key={chat._id} className="flex items-center m-2 bg-yellow-100 p-4 rounded-md" onClick={() => setCurrentChat(chat)}>
                    {/* Render the other user's profile picture */}
                    <img src={chat.members.filter(member => member._id !== user._id)[0].profilePicture} alt="User" className="w-10 h-10 rounded-full mr-2" />
                    <div className="flex flex-col">
                      <span className="text-sm font-medium">{chat.members.filter(member => member._id !== user._id)[0].name}</span>
                      <span className="text-sm font-light"> {checkOnlineStatus(chat)? "Online": "Offline"}  </span> 
                    </div>
                  </div>
                ))}
              </div>
            </>
          )}
        </div>
        {/* Right Section */}
        <ChatBox chat={currentChat} currentUser={user._id} setSendMessage={setSendMessage}
          receivedMessage={receivedMessage}/>
      </div>
      <Footer />
    </div>
  );
};

export default Chat;
