import React, { useEffect, useState } from 'react'

const ChatBox = ({chat , currentUser}) => {

  const [userData , setUserData] = useState([]); 

  useEffect(()=>
  {
    const user = chat?.members.filter(member => member._id !== currentUser ); 
    console.log(user);  
  },[chat,currentUser]); 

  
  return (
    <div>
        <div class="w-3/4 bg-white rounded-lg shadow-lg p-4 flex-grow">
            <div class="flex items-center m-2 bg-blue-100 p-4 rounded-md">
                    <img src={user?.profilePicture} alt="User 2" class="w-10 h-10 rounded-full mr-2"/>
                    <div class="flex flex-col">
                      <span class="text-sm font-medium">{user?.name}</span>
                    </div>
            </div>
            <div class="h-64 overflow-y-auto mb-4">
                {/*Messages*/}
            </div>
             {/* Chat Input Area */}
            <form>
                <textarea class="w-full border border-gray-300 rounded-lg p-2" rows="3" placeholder="Type your message here..."></textarea>
                <button type="submit" class="mt-2 bg-blue-500 text-white py-2 px-4 rounded-lg">Send</button>
            </form>
        </div>
    </div>
  )
}

export default ChatBox ;
